CREATE TABLE `meemprestaai`.`tb_usuarios` (`id` INT NOT NULL AUTO_INCREMENT , `nome` VARCHAR(100) NOT NULL , `localizacao` MULTIPOINT(100) NOT NULL , `cep` INT(9) NOT NULL , `interesses` VARCHAR(155) NOT NULL , `documento` VARCHAR(155) NOT NULL , `tipo` INT(2) NOT NULL , `numeroDeEmprestimos` INT(155) NOT NULL , `avaliacaoId` INT(155) NOT NULL , `endereco` VARCHAR(155) NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB; 